import React, { useState } from 'react';
import NavBar from './Components/NavBar'; // Đảm bảo bạn đã nhập đúng NavBar
import MovieComponent from './Components/MovieComponent'; // Nhập MovieComponent
import data from './data'; // Nhập dữ liệu từ file data
import './App.css'; // Đảm bảo bạn có các kiểu CSS cần thiết

function App() {
  const [title, setTitle] = useState('Weather with you');
  const [description, setDescription] = useState('Corrupt politicians, frenzied nationalists, and other warmongering forces constantly jeopardize the thin veneer of peace between neighboring countries Ostania and Westalis.');

  const handleMovieClick = (item) => {
    setTitle(item.movieName);
    setDescription(item.description);
  };

  return (
    <div className="App">
      <div className="container">
        <NavBar />
        <div className="explore"><h2>Explore</h2></div>
        <div className="what"><h3>What are you gonna watch today?</h3></div>
        <div className="banner">
          <div className="title">{title}</div>
          <div className="description">{description}</div>
        </div>
        <div className="movie-list">
          {data.map((movie) => (
            <MovieComponent key={movie.id} movie={movie} onClick={() => handleMovieClick(movie)} />
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
